mapper.insert:
INSERT INTO STUDENT_T (
    STUDENTNO
  , NAME
  , KOREAN
  , ENGLISH
  , MATH
) VALUES (
    STUDENT_SEQ.NEXTVAL
  , #{name}
  , #{korean}
  , #{english}
  , #{math}
)

mapper.update:
UPDATE STUDENT_T
   SET KOREAN = #{korean}
     , ENGLISH = #{english}
     , MATH = #{math}
 WHERE STUDENTNO = #{studentNo}

mapper.select:
SELECT STUDENTNO, NAME, KOREAN, ENGLISH, MATH
  FROM STUDENT_T
 WHERE STUDENTNO = #{studentNo}

SELECT STUDENTNO, NAME, KOREAN, ENGLISH, MATH
  FROM STUDENT_T
 WHERE STUDENTNO = #{studentNo}
